import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import ScrollToBottom from "react-scroll-to-bottom";

import InfoBar from "../InfoBar/InfoBar";
import Input from "../Input/Input";
import Messages from "../Messages/Messages";
import UserContainer from "../UserContainer/UserContainer";

let socket;

const Chat = ({ location }) => {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [users, setUsers] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  const ENDPOINT = "localhost:8000";

  useEffect(() => {
    const { name, room } = queryString.parse(location.search);

    socket = io(ENDPOINT);

    setName(name);
    setRoom(room);

    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
    });

    return () => {
      socket.emit("disconnect");
      socket.off();
    };
  }, [ENDPOINT, location.search]);

  useEffect(() => {
    socket.on("message", (message) => {
      setMessages([...messages, message]);
    });

    socket.on("roomData", ({ users }) => {
      setUsers(users);
    });
  }, [messages]);

  const sendMessage = (event) => {
    event.preventDefault();

    if (message) {
      socket.emit("sendMessage", message, () => setMessage(""));
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900">
      <div className="flex flex-col justify-between bg-white rounded-lg h-[70vh] w-full max-w-4xl shadow-lg">
        <div className="grid grid-cols-[275px_1fr] grid-rows-[71px_1fr_78px] h-full">
          <div className="bg-green-500 col-span-1 row-span-1 flex items-center px-5 rounded-tl-lg shadow-md z-10">
            <h1 className="text-white font-bold">Chat App</h1>
          </div>

          <div className="bg-green-500 col-span-1 row-span-1 overflow-y-auto">
            <UserContainer users={users} />
          </div>

          <div className="bg-green-500 col-span-1 row-start-3 border-t border-opacity-25 rounded-bl-lg"></div>

          <div className="bg-gray-100 col-start-2 row-span-1 grid grid-cols-[1fr_36px] items-center px-5 text-blue-700 font-bold text-lg rounded-tr-lg shadow-md">
            <InfoBar room={room} />
          </div>

          <ScrollToBottom className="col-start-2 row-start-2 overflow-y-auto">
            <Messages messages={messages} name={name} />
          </ScrollToBottom>

          <div className="bg-gray-100 col-start-2 row-start-3 border-t border-opacity-25 rounded-br-lg">
            <Input
              message={message}
              setMessage={setMessage}
              sendMessage={sendMessage}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
