import React from 'react';

import closeIcon from '../../images/closeIcon.png';

// import './InfoBar.css';

const InfoBar = ({ room }) => (
  <div className="infoBar flex justify-between items-center bg-blue-600 p-4 rounded-md shadow-md">
    <div className="leftInnerContainer flex items-center">
      <h3 className="text-white text-lg font-semibold">{room}</h3>
    </div>
    <div className="rightInnerContainer">
      <a href="/">
        <img src={closeIcon} alt="close" className="h-6 w-6 text-white" />
      </a>
    </div>
  </div>
);

export default InfoBar;
